import React from 'react'
import styled from 'styled-components'


const SignUpForm = styled.div`
    position: relative;
    width:100%;
    height: 100%;
`;

const Input = styled.input`
    width: 75%;
    height: 30px;
    background: #e0dede;
    display: flex;
    justify-content: center;
    margin: 20px auto;
    padding: 20px;
    border: none;
    outline: none;
    border-radius: 5px;
`;

const Button = styled.button`
    width: 70%;
    height: 50px;
    margin: 10px auto;
    justify-content: center;
    display: block;
    color: #fff;
    background: #573b8a;
    font-size: 1em;
    font-weight: bold;
    margin-top: 20px;
    outline: none;
    border: none;
    border-radius: 5px;
    transition: .2s ease-in;
    cursor: pointer;

    &:hover{
      background: #6d44b8;
    }
`;

const ButtonSignUp = styled.button`
    background: transparent;
    outline: none;   
    border: none;
    color: #fff;
    font-size: 2.3em;
    display: flex;
    justify-content: center;
    margin: 60px;
    font-weight: bold;
    cursor: pointer;
    transition: .5s ease-in-out;
    margin: 60px auto;
`;


export const SingUpForm = ({ buttonState }) => {
  return (
    <SignUpForm>
        <form>
                <ButtonSignUp
                style={{transform: buttonState ? "scale(.6)" : " scale(1)" }}
                >
                Sign Up
                </ButtonSignUp>
                <Input 
                type='text'
                placeholder="User name"
                />
                <Input 
                type='text'
                placeholder="Email"
                />
                <Input 
                type='text'
                placeholder="Password"
                />
                <Button>Sign Up</Button>
        </form>
    </SignUpForm>
  )
}
