import React from "react";
import styled from "styled-components";

import './App.css'

const Wrapper = styled.div`
    margin: 0;
    padding: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    font-family: 'Jost', sans-serif;
    background: linear-gradient(to bottom, #0f0c29, #302b63, #24243e);
`;

const Form = styled.div`
    width: 350px;
    height: 500px;
    background: red;
    overflow: hidden;
    background: url("https://doc-08-2c-docs.googleusercontent.com/docs/securesc/68c90smiglihng9534mvqmq1946dmis5/fo0picsp1nhiucmc0l25s29respgpr4j/1631524275000/03522360960922298374/03522360960922298374/1Sx0jhdpEpnNIydS4rnN4kHSJtU1EyWka?e=view&authuser=0&nonce=gcrocepgbb17m&user=03522360960922298374&hash=tfhgbs86ka6divo3llbvp93mg4csvb38") no-repeat center/ cover;
    border-radius: 10px;
    box-shadow: 5px 20px 50px #000;
`;

const SignUp = styled.div`
    position: relative;
    width:100%;
    height: 100%;
`;

const Input = styled.input`
    width: 75%;
    height: 30px;
    background: #e0dede;
    display: flex;
    justify-content: center;
    margin: 20px auto;
    padding: 20px;
    border: none;
    outline: none;
    border-radius: 5px;
`;

const ButtonSignUp = styled.button`
    background: transparent;
    outline: none;   
    border: none;
    color: #fff;
    font-size: 2.3em;
    display: flex;
    justify-content: center;
    margin: 60px;
    font-weight: bold;
    cursor: pointer;
    transition: .5s ease-in-out;
    margin: 60px auto;
`;

const Button = styled.button`
    width: 70%;
    height: 50px;
    margin: 10px auto;
    justify-content: center;
    display: block;
    color: #fff;
    background: #573b8a;
    font-size: 1em;
    font-weight: bold;
    margin-top: 20px;
    outline: none;
    border: none;
    border-radius: 5px;
    transition: .2s ease-in;
    cursor: pointer;

    &:hover{
      background: #6d44b8;
    }
`;

const ButtonLogin = styled.button`
    background: transparent;
    outline: none;   
    border: none;
    color: #fff;
    font-size: 2.3em;
    display: flex;
    justify-content: center;
    margin: 60px;
    font-weight: bold;
    cursor: pointer;
    transition: .5s ease-in-out;
    margin: 60px auto;
    color: #573b8a;
	  transform: scale(.6);
`;

const Login = styled.div`
    height: 460px;
    background: #eee;
    border-radius: 60% / 10%;
    transform: translateY(-170px);
    transition: .8s ease-in-out;
`;



function App() {
  const [ buttonState, setButtonState ] = React.useState(false);

  console.log(buttonState);
  const handleButtonState = () =>{
  setButtonState(!buttonState);
}


  return (
    <Wrapper>
      <Form>
        <SignUp>
          <form>
            <ButtonSignUp 
              onClick={handleButtonState}
              style={{ }}
            >
              Sign Up
            </ButtonSignUp>
            <Input 
              type='text'
              placeholder="User name"
            />
            <Input 
              type='text'
              placeholder="Email"
            />
            <Input 
              type='text'
              placeholder="Password"
            />
            <Button>Sign Up</Button>
          </form>
        </SignUp>
        <Login
          style={{ transform: handleButtonState ? "translateY(-180px)" : " translateY(-180px)" }}
        >
          <ButtonLogin>Login</ButtonLogin>
          <Input 
              type='text'
              placeholder="Email"
            />
            <Input 
              type='text'
              placeholder="Password"
            />
            <Button onClick={handleButtonState}>Login</Button>

        </Login>
      </Form>
    </Wrapper> 
  );
}

export default App;
