import * as Yup from 'yup';

export const useSchema = yup.object().shape({
    name: yup.number().typeError('Error').required("Обов'язково"),
    email: yup.string().email().required("Обов'язково"),
    password: yup.string().min(4).max(20).required("Обов'язково"),
    confirmPassword: yup.string().oneOf([yup.ref('password')], 'Пароли не співпадають')
  })
